INSERT INTO %pfx%system_contents (id, page_id, version_id, title, content, template, type, mdate, cdate, hide, sort, box_id, owner_id) VALUES(62, 6, 58, 'About me', '<p>Li Europan lingues es membres del sam familie.  Lor separat existentie
